var HelpVue=new Vue({el:"#help-vue",data:{menus:[],searchText:undefined},watch:{searchText:function(n){var t,i;for(n&&(n=n.toLocaleLowerCase()),t=0;t<this.menus.length;t++){let r=this.menus[t].articles;for(i=0;i<r.length;i++)r[i].isShow=r[i].keyWords.search(n)>=0||r[i].title.toLocaleLowerCase().includes(n);this.menus[t].isShow=this.menus[t].articles.filter(n=>n.isShow==!0).length>0}}},mounted:function(){this.load()},methods:{load:function(){return $.ajax({type:"GET",url:"/Help/Center/LoadMenu?id="+this.articleID,context:this,contentType:"application/json",dataType:"json",success:function(n){n.isOk&&(this.menus=n.menus)}})},getDateByFormat:function(n,t){return GetDateByFormat(n,t)},selectedMenu:function(n){$("html, body").animate({scrollTop:$("#menu_"+n.id).offset().top-75},1e3)}}});